import './App.css';
import React, { useState } from 'react';
import styled from 'styled-components';
import  CardTemp  from './components/CardTemp';
import  CardTermo  from './components/CardTermo';
import  CardP  from './components/CardP';
import  Card  from './components/Card';
import Modo from './Modo';
const lightTheme = {
  background: '#eee',
  text: '#333',
};

const darkTheme = {
  background: '#333',
  text: '#eee',
};


const AppTotal = styled.div`
  display: flex;
  width: 100%;
 background-color: ${(props) => props.theme.background};
  color: ${(props) => props.theme.text}; 
`;

const LeftColumn = styled.div`
  flex: 0 0 30%; /* Ocupa el 30% del ancho disponible */
  padding: 16px; /* A単ade espaciado si es necesario */
  box-sizing: border-box;
`;

const RightColumn = styled.div`
  flex: 0 0 70%; /* Ocupa el 70% del ancho disponible */
  padding: 16px; /* A単ade espaciado si es necesario */
  box-sizing: border-box;
`;

function App() {
 const [isDarkMode, setIsDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };


  return (
    <AppTotal>
   <CardTermo/>
        <CardTemp/>
        <LeftColumn>

      </LeftColumn>
      <RightColumn>
<CardP/>
<Card/>
 <Modo toggleDarkMode={toggleDarkMode} isDarkMode={isDarkMode} />
  </RightColumn>
    </AppTotal>
  );
}

export default App;
