import React, { useState } from 'react';
import  Card  from './Card';
import styled from 'styled-components';
import { UserData } from '../data';
import BarChart from './BarChart';

const CardBoxDiv = styled.div`  
  display: grid;
  grid-template-columns: repeat(3, 2fr); 
   background-:transparent;
  @media (max-width: 768px) {
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));   }

  @media (max-width: 480px) {
    grid-template-columns: repeat(auto-fit, minmax(100px, 1fr)); 
  }
`;

const Barradiv = styled.div`
 color: black; /* Establece el color del texto en blanco */

  max-width: 250px; /* Establece un ancho máximo opcional */
  margin: 0 auto; /* Centra el gráfico horizontalmente */
  font-size: 16px; /* Tamaño de fuente predeterminado */
   background-image: linear-gradient(135deg, #f83600 0%, #f9d423 100%); 
   border-radius:16px;

`;
function CardBox() {
const labels = []; // Reemplaza con tus etiquetas reales
  const data = [];
  const [userData, setUserData] = useState({    
      labels: '%Humedad'  ,   
      datasets: [
        {
          label: "Temperatura/hora",
          data: 60,
      backgroundColor: 'lightblue',              
        }
      ]
    
  });

  
  
    return (
      <CardBoxDiv>
        <Barradiv>
       <BarChart chartData={userData}/></Barradiv>
        
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        


      </CardBoxDiv>
        
        
    );
 
}

export default CardBox;
