import React, { Component } from 'react'
import styled from 'styled-components';

const TotalTemp = styled.div`

  height: 300px;
  border-radius: 5%;
  margin:32px;
  display: flex;
  width: 100%;
border-radius:32px;

 background-image: linear-gradient(-220deg, #f83600 0%, #f9d423 100%); 
`;

const LeftColumn = styled.div`
  flex: 0 0 50%; /* Ocupa el 30% del ancho disponible */
  padding: 16px; /* A単ade espaciado si es necesario */
  box-sizing: border-box;

`;

const RightColumn = styled.div`
  flex: 0 0 50%; /* Ocupa el 70% del ancho disponible */
  padding: 16px; /* A単ade espaciado si es necesario */
  box-sizing: border-box;
`;
export class CardTemp extends Component {
  render() {
    return (
     <TotalTemp>
    <LeftColumn>
Derecha
    </LeftColumn>
<RightColumn>
Izquierda
</RightColumn>
     </TotalTemp>
    )
  }
}

export default CardTemp
